import React, { useEffect, useState } from "react";
import { UserRound } from "lucide-react";
import { MapPin } from "lucide-react";
import { Phone } from "lucide-react";
import './Header.css';
const Header = () => {
  const [show, handleShow] = useState(false);

  const transitionNavBar = () => {
    if (window.scrollY > 180) {
      handleShow(true);
    } else {
      handleShow(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", transitionNavBar);
    return () => window.removeEventListener("scroll", transitionNavBar);
  }, []);
  return (
    <div className={`nav${show && 'nav_black'}`}>
      <div className=" bg-red-800 h-auto w-full md:mt-12 ">
        <div className=" flex space-x-5 mt-5 md:mt-0 md:justify-evenly md:items-center ">
          <div className=" hidden xl:flex font-bold items-center text-white ">
            <UserRound />
            <p className="">Foll0w Us</p>
            <div className=" md:flex">
              <img src="./assets/icons/facebook.svg" alt="skj" />
            </div>
          </div>
          <div className=" hidden xl:flex font-bold items-center text-white ">
            <Phone />
            <p>CALL NOW:</p>
          </div>
          <div>
            <h2 className=" text-white">+1(347)466-3991</h2>
          </div>
          <div className=" hidden xl:flex font-bold items-center text-white">
            <MapPin />
            <p>Location</p>
            <div>
              <p className=" text-white">114 E Fordham Rd, Bronx, NY 10468</p>
            </div>
          </div>
        </div>

        <div className="flex justify-between items-center mt-11 border shadow-md">
          <div className="bg-white h-20 w-full flex items-center ">
            <div className="ml-10 mr-5">
              <img
                src="assets/icons/hero.jpeg"
                className="h-16 w-16 mr-5"
                alt="sk"
              />
            </div>
            <div className="flex flex-wrap justify-around items-center flex-grow">
              <a href="#" className="m-3 float-left">
                HOME
              </a>
              <a href="#" className="m-3">
                ABOUT US
              </a>
              <a href="#" className="m-3">
                SOLUTIONS
              </a>
              <a href="#" className="m-3">
                SERVICES
              </a>
              <a href="#" className="m-3">
                LOCATIONS
              </a>
              <a href="#" className="m-3">
                CONTACT
              </a>
            </div>
            <div className="mr-10 md:p-4 md:m-2  ml-5">
              <button className="bg-red-800 text-white p-3 m-1">
                BOOK AN APPOINTMENT
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
